import { put, takeEvery, call } from "redux-saga/effects";
import axios from "axios";
import {loadTableResultsSuccess} from '../actions/table.actions';

function fetchUserProfile() {
  return axios.get(
    "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/significant_week.geojson"
  );
}

export function* workerGetUser() {
  try {
    let resp = yield call(fetchUserProfile);

    yield put(loadUserSuccess(resp.data))
  } catch (e) {
    console.error(e);
  }
}

export function* watchGetUser() {
  yield takeEvery("LOAD_TABLE_RESULTS", workerGetTableResults);
}
