import { put, takeEvery, call, all, select } from 'redux-saga/effects';
import axios from 'axios';
import {LOAD_TABLE_RESULTS_SUCCESS} from '../actions/table.actions';

function fetchQuakes() {
    return axios.get('https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/significant_week.geojson');
}

export function* workerGetTableResults() {
    try {
        console.log('handleLoadQuakes');
        let resp = yield call(fetchQuakes);

        yield put({
            type: 'LOAD_TABLE_RESULTS_SUCCESS',
            quakes: resp.data,
        });
    } catch (e) {
        console.error(e);
    }
}

export function* watchRefreshQuakes() {
    yield takeEvery('LOAD_TABLE_RESULTS', workerGetTableResults);
}
